# Function to send request and handle response
def get_movie_info(movie_name):
    params = {
        'apikey': api_key,
        't': movie_name
    }
    response = requests.get(base_url, params=params)
    if response.status_code in [200, 404]:
        return response.json(), response.status_code
    else:
        return {'status': response.status_code, 'message': response.reason}, response.status_code

# Main function
def main():
    # Update your file path according to your system
    csv_file_path = "OMDb_byIdOrTitle_modified_1000.csv"
    
    # Open CSV file for writing (this will overwrite existing content)
    with open(csv_file_path, 'w', newline='', encoding='utf-8') as csvfile:
        fieldnames = ['testCaseId', 'queryParameters', 'operationId', 'path', 'httpMethod', 'headerParameters', 
                      'pathParameters', 'formParameters', 'bodyParameter', 'statusCode', 'responseBody']
        writer = csv.DictWriter(csvfile, fieldnames=fieldnames)
        writer.writeheader()

        # Loop through movie names
        for i, movie_name in enumerate(movie_names):
            movie_info, status = get_movie_info(movie_name)
            print("API Call", i, "\nStatus Code", status)
            response_body = json.dumps(movie_info)
            writer.writerow({
                'testCaseId': i,
                'queryParameters': f'apikey={api_key}&t={movie_name}',
                'operationId': 'searchByIdOrTitle',
                'path': '/',
                'httpMethod': 'GET',
                'headerParameters': '',
                'pathParameters': f't={movie_name}',
                'formParameters': '',
                'bodyParameter': '',
                'statusCode': status,
                'responseBody': response_body
            })

if __name__ == '__main__':
    main()
